input_string = ''


def unique(s: str) -> bool:
    return len(set(s)) == len(s)


def get_position(_input_string: str, number_of_characters: int) -> int:
    for idx, character in enumerate(input_string):
        sub_string = _input_string[idx:idx+number_of_characters]
        if unique(sub_string):
            return idx + number_of_characters


position = get_position(input_string, number_of_characters=14)
print(f'First marker after {position} characters')
